INSERT INTO `tracks_single_page` VALUES ('3', 'About Wedo', '<p class=\"about-title\">\n	<strong>To Know Us</strong> \n</p>\n<p class=\"about-text\">\n	<span style=\"color:#666666;\"><strong>WEDO</strong> Express is founded in 2014 as a logistic company in China, using advanced technology, access to global resources, and an integrated network of physical, technological, and human assets, We provides a powerful competitive advantage that can help you earn repeat customers and grow your business.</span> \n</p>\n<p class=\"about-text\">\n	<span style=\"color:#666666;\">WEDO has grown into the authority of the logistics industry by clearly focusing on the goal of enabling commerce around the globe.</span> \n</p>\n<p class=\"about-text\">\n	<span style=\"color:#666666;\">WEDO  is a global company with one of the most trusted and admired brands in the world.We\nhave become the world‘s the most populer package delivery company and a leading\nglobal provider of specialized transportation and logistics services. in the\nfirst 3 months the website opened, the total number of visitors are more than 50000.</span> \n</p>\n<p class=\"about-text\">\n	<span style=\"color:#666666;\">Every day, we manage the flow of goods, funds, and information in more than 100 countries and territories worldwide.</span> \n</p>\n<p class=\"about-title\">\n	<strong>Your trust is our power to go on!</strong> \n</p>', '0', '0', '1405925152', '71', '1407150207', '71', '0');
